```c
#include <iostream>
#include <numeric>
#include <vector>
#include <cmath>
#include <algorithm>

int powersum(int n, int k)
{
	std::vector<int> powers(n);
	std::iota(powers.begin(), powers.end(), 1);
	std::transform(powers.begin(), powers.end(), powers.begin(), [k](int i) {
	return int(std::pow(double(i), double(k)));
	});
	int sum = std::accumulate(powers.begin(), powers.end(), 0);
	return sum;
}
```

