std::string hash_string(const std::string& input) {
  unsigned char hash[MD5_DIGEST_LENGTH];
  MD5((unsigned char*)input.c_str(), input.length(), (unsigned char*)&hash);
  char buf[2 * MD5_DIGEST_LENGTH + 1];
  for (int i = 0; i < MD5_DIGEST_LENGTH; i++)
    sprintf(&buf[i * 2], "%02x", (unsigned int)hash[i]);
  return std::string(buf);
}

void decrypt_md5(const std::string& hash, const std::string& rainbow_table) {
