    <% for (const child of root.children) { %>
      <li>
        <%= child.name %>
        <% if (child.children) { %>
          <ul>
            <% for (const grandChild of child.children) { %>
              <li>
                <%= grandChild.name %>
                <% if (grandChild.children) { %>
                  <ul>
