```python
from flask import Flask, request, jsonify
from flask_pymongo import PyMongo
from bson.objectid import ObjectId

app = Flask(__name__)
app.config["MONGO_URI"] = "mongodb://localhost:27017/mydatabase"
mongo = PyMongo(app)

@app.route("/api/notes", methods=["POST"])
def add_note():
    note = {
        "text": request.json["text"],
        "userId": request.json["userId"],
    }
    mongo.db.notes.insert_one(note)
    return jsonify(str(note["_id"]))

@app.route("/api/notes", methods=["GET"])
def get_notes():
    user_id = request.args.get("userId")
    notes = mongo.db.notes.find({"userId": user_id})
    return jsonify([{"_id": str(note["_id"]), "text": note["text"]} for note in notes])

@app.route("/api/images", methods=["POST"])
def add_image():
    image = {
        "data": request.files["file"].read(),
        "userId": request.form["userId"],
    }
    mongo.db.images.insert_one(image)
    return jsonify(str(image["_id"]))

@app.route("/api/images", methods=["GET"])
def get_images():
    user_id = request.args.get("userId")
    images = mongo.db.images.find({"userId": user_id})
    return jsonify([{"_id": str(image["_id"]), "data": image["data"].decode()} for image in images])

if __name__ == "__main__":
    app.run()

```
