def to_base(n: int, b: int = 10) -> str:
    # modified from https://stackoverflow.com/questions/2267362/how-to-convert-an-integer-to-a-string-in-any-base
    assert b <= len(BS), (
        f"unsupported base {b}. At most {len(BS)} is supported. "
        "Increase the BS string if more is required"
    )
    assert n >= 0, "Negative numbers are not supported"
    result = ""
    while n >= 0:
        result += BS[n % b]
        n //= b
    return result[::-1] or "0"


def add(a: int, b: int, base: int = 10) -> str:
