public class Maybe<T>
{
    private readonly T _value;

    private Maybe(T value)
    {
        _value = value;
    }

    public static Maybe<T> Some(T value) => new Maybe<T>(value);
    public static Maybe<T> None => new Maybe<T>(default(T));

    public TValue Match<TValue>(Func<T, TValue> some, Func<TValue> none) =>
        _value == null ? none() : some(_value);

    public void Match(Action<T> some, Action none) =>
        _value == null ? none() : some(_value);

    public Maybe<TResult> Select<TResult>(Func<T, TResult> selector) =>
        _value == null ? Maybe<TResult>.None : Maybe<TResult>.Some(selector(_value));

    public Maybe<TResult> SelectMany<TResult>(Func<T, Maybe<TResult>> selector) =>
        _value == null ? Maybe<TResult>.None : selector(_value);
